import './ProjectTile.css';
import {React, useState} from 'react';

export const ProjectTile = () => {
    const [press, setPress] = useState(true);
    const message = 'Please tell us about yourself and your skills, and we will forward the information to the project founder. \nили хуйните продолжение анекдота';


    const handleClick = (e) => {
        e.preventDefault();
        setPress = press ? false : true;
    }

    const renderButton = () => {
        if(press)
        {
            return (
                <div>
                    <form>
                        <div className = 'input'>
                            <textarea defaultValue ={message} rows = {5} cols ={55}/>
                        </div>
                        <div className='apply'>
                            <button type='submit' onClick = {handleClick}>Apply</button>
                        </div>
                    </form>
                </div>
            )
        }
        else 
        {
            return (
                    <div className='apply'>
                        <button onClick = {handleClick}>Apply</button>
                    </div>
                )
        }
    }

    return (
        <div className='outer'>
            <div className="title">
                <h1>Eto ProjectTile</h1>
            </div>
            <div className="description">
                <h2>— Капитан, справа по борту шестивесельный шлюп!<br/>
                    Через некоторое время:<br/>
                    — Капитан, слева по борту шестивесельный шлюп!<br/>
                    — Боцман, стряхните м@ндавошку с окуляра!</h2>
            </div>
            <div className="skills">
                {/* props.skills.map((skill) => {
                    return <button>{skill.value}</button};   
                }*/}
                
                <button>Ebat</button>
                <button>Anekdot</button>
                <button>Pro</button>
                <button>Bocmana</button>
                <button>Nahui</button>
            </div>
            {renderButton()}
        </div>
    )
}
