import './ProjectTile.css';
import {React, useState} from 'react';

export const ProjectTile = () => {
    const [press, setPress] = useState(false);
    const message = 'Please tell us about yourself and your skills, and we will forward the information to the project founder. \nили хуйните продолжение анекдота';


    const handleClick = (e) => {
        e.preventDefault();
        setPress(press ? false : true);
    }

    const renderButton = () => {
        if(press)
        {
            return (
                <div>
                    <form>
                        <div className = 'input' name='message'>
                            <textarea defaultValue ={message}/>
                        </div>
                        <div className='apply'>
                            <button type='submit' onClick = {handleClick}>Send</button>
                        </div>
                    </form>
                </div>
            )
        }
        else 
        {
            return (
                    <div className='apply'>
                        <button onClick = {handleClick}>Apply</button>
                    </div>
                )
        }
    }

    return (
        <div className='outer'>
            <div className="title">
                <h1>{props.project.name}</h1>
            </div>
            <div className="description">
                <h2>{props.project.description}</h2>
            </div>
            <div className="skills">
                {props.project.skills.map((skill) => {
                    return <button>{skill.name}</button>})   
                }
                
                <button>Nahui</button>
            </div>
            {renderButton()}
        </div>
    )
}
